section.programlisting:
# vim /etc/syslog-ng/syslog-ng.conf
(... Add the next line ...)
source jail { unix-stream("/var/named/chroot/dev/log"); };

options {
  directory "/var/bind";
  pid-file "/var/run/named/named.pid";
  statistics-file "/var/run/named/named.stats";
  listen-on { 127.0.0.1; };
  listen-on-v6 { 2001:db8:81:21::1bac:98ad:5fe1; };
  allow-query { any; };
  zone-statistics yes;
  allow-transfer { 2001:db8:81:22::7aae:6b01:e3d8; };
  notify yes;
  recursion no;
  version "[nope]";
}

# Access to DNS for local addresses (i.e. genfic-owned)
view "local" {
  match-clients { 2001:db8:81::/48; };
  recursion yes;
  zone "genfic.com" { type master; file "pri/com.genfic"; };
  zone "1.8.0.0.8.b.d.0.1.0.0.2.ip6.arpa" { type master; fiel "pri/inv.com.genfic"; };
}

# cat /var/named/chroot/var/bind/pri/com.genfic
$ORIGIN genfic.com.;
$TTL 1h;
genfic.com. IN SOA ns.genfic.com. (
  2012041101  ; serial
  1d          ; slave refresh
  2h          ; slave retry
  4w          ; slave expiration
  1h          ; max caching time
)

genfic.com.  NS  ns;
genfic.com.  NS  ns2.genfic.com;
genfic.com.  MX  10  mail.genfic.com.;
genfic.com.  MX  20  mail2.genfic.com.;

genfic.com.  AAAA 2001:db8:81:80::41dd:13ed:c49e;
ns           AAAA 2001:db8:81:21::1bac:98ad:5fe1;
ns2          AAAA 2001:db8:81:22::7aae:6b01:e3d8;
www          CNAME genfic.com.;
mail         AAAA 2001:db8:81:21::d3b0:0738:8ad5;
mail2        AAAA 2001:db8:81:22::d850:5e9f:e569;
; (...)

# cat /var/named/chroot/var/bind/pri/inv.com.genfic
$TTL 1h;
$ORIGIN 1.8.0.0.8.b.d.0.1.0.0.2.IP6.ARPA.
@   IN   SOA   ns.genfic.com. (
  2012041101;
  1d;
  2h;
  4w;
  1h;
)

   IN    NS    ns.genfic.com.
   IN    NS    ns2.genfic.com.
1.e.f.5.d.a.8.9.c.a.b.1.0.0.0.0    IN   PTR    ns.genfic.com.
; (...)

# cat /etc/dhcp/dhcpd.conf
option domain-name genfic.com;
option domain-name-servers 2001:db8:81:21::1bac:98ad:5fe1, 2001:db8:81:22::7aae:6b01:e3d8;
ddns-update-style interim;
ddns-rev-domainname "in-addr.arpa";

authorative;

default-lease-time 600;
max-lease-time 7200;

key primaryserver {
  algorithm hmac-md5;
  secret "nope";
};

omapi-key primaryserver;
omapi-port 7911;

failover peer "genfic" {
  primary;
  address 2001:db8:81:21::19f4:3303:40f4;
  port 519;
  peer address 2001:db8:81:22::a05f:3853:fe78;
  peer port 519;
  max-response-delay 30;
  max-unacked-updates 10;
  load balance max seconds 3;
  mclt 600;
  split 128;
};

subnet6 2001:db8:81:e2::/64 {
  range6 2001:db8:81:e2::0f00 2001:db8:81:e2:f:0fff;
  option dhcp6.name-servers 2001:db8:81:21::1bac:98ad:5fe1, 2001:db8:81:22::7aae:6b01:e3d8;
  option dhcp6.domain-search "workstation.internal.genfic.com";
}
